mapper.update:
update strategy_award
set award_count_surplus = award_count_surplus - 1
where strategy_id = #{strategyId}
  and award_id = #{awardId}
  and award_count_surplus > 0

mapper.select:
select id, award_title
from strategy_award
where strategy_id = #{strategyId}
  and award_id = #{awardId}
